import React, {useState} from 'react'


export default function TextForm(props) {
    const handleUpClick = ()=>{
        // console.log("Uppercase was clicked: " +  text);
        let newText = text.toUpperCase();
        setText(newText)
    }

      const handleUpClickk = ()=>{
        // console.log("Uppercase was clicked: " +  text);
        let newText = text.toLowerCase();
        setText(newText)
    }

    const handleUpClear = ()=>{
        // console.log("Uppercase was clicked: " +  text);
        let newText = '';
        setText(newText)
    }
    

    const handleOnChange = (event)=>{
        // console.log("On change");
        setText(event.target.value)
    }

    const handleCopy=()=>{
        var text=document
        text.select();
         navigator.clipboard.writeText(text.value);
    }

    const [text, setText] = useState('Enter text here2'); 
    // text = "new text"; // Wrong way to change the state
    // setText("new text"); // Correct way to change the state
    return (
        <>
        <div className="container"> 
            <h1>{props.heading}</h1>
            <div className="mb-3"> 
            <textarea className="form-control" value={text} onChange={handleOnChange} id="myBox" rows="8"></textarea>
            </div>
            <button className="btn btn-primary mx-4" onClick={handleUpClick}>Convert to Uppercase</button>

            <button className='btn btn-primary my-4' onClick={handleUpClickk}>convert to lowercase</button>
            
            <button className='btn btn-primary my-4' onClick={handleUpClear}>clear</button>

            <button className='btn btn-primary my-4' onClick={handleCopy}>copy</button>
        </div>
      
        <div className="conatiner-my3">
            <h1>your text summary</h1>
            <p>{text.split("").length} words and {text.length} charecters</p>
            <p>{0.008*text.split("").length}minutes require to read</p>
            <h2>Preveiw</h2>
            <p>{text}</p>
        </div>
        </>
    )


}
